mapper.update:
UPDATE BOARD
SET BOARD_COUNT = BOARD_COUNT +1
WHERE ID=#{id}

mapper.insert:
INSERT INTO BOARD_FILE(ID
					  , BOARD_ID
					  , FILE_NAME
					  , FILE_ORIGINAL
					  , REGI_ID
					  , REGI_DT
					  , UPDT_ID
					  , UPDT_DT)
				VALUES(BOARD_FILE_SEQ.NEXTVAL
					  , #{board_id}
					  , #{file_name}
					  , #{file_original}
					  , #{regi_id}
					  , SYSDATE
					  , #{updt_id}
					  , SYSDATE)

mapper.update:
UPDATE BOARD
		SET BOARD_TITLE=#{board_title}
   		   ,BOARD_CONTENT=#{board_content}
   		   ,UPDT_ID=#{regi_id}
   		   ,UPDT_DT=SYSDATE
		WHERE ID=#{id}
